class PersonsSerializationTest {

    @Test
    fun personImagesShouldReturnFilePath() {
        runBlocking {
            val mockEngine = MockEngine { request ->
                respond(
                    content = ByteReadChannel("{\"id\":100,\"profiles\":[{\"aspect_ratio\":0.667,\"height\":1843,\"iso_639_1\":null,\"file_path\":\"/d0fzWMIzsMAXS2M04SKFMof6zQX.jpg\",\"vote_average\":5.312,\"vote_count\":1,\"width\":1229},{\"aspect_ratio\":0.667,\"height\":450,\"iso_639_1\":null,\"file_path\":\"/tGgD96WOhmuQU4e6ZTU5IEx4W9E.jpg\",\"vote_average\":5.246,\"vote_count\":2,\"width\":300}]}"),
                    status = HttpStatusCode.OK,
                    headers = headersOf(HttpHeaders.ContentType, "application/json")
                )
            }
            val personImagesService = PersonImagesService(
                HttpClient(mockEngine) {
                    install(Logging) {
                        level = LogLevel.ALL
                    }
                    install(JsonFeature) {
                        serializer = KotlinxSerializer()
                    }
                })
            assertEquals("/d0fzWMIzsMAXS2M04SKFMof6zQX.jpg", personImagesService.getPersonImages(100).profiles[0].file_path)
        }
    }

